mapper.update:
update post 
set 
	post_title = #{title},
	category_id = #{categoryId}
where 
	post_id = #{postId}

mapper.select:
select p.post_id postId, post_title title, write_date writeDate, p.content_id contentId, board_id boardId, views, p.user_id writerId, category_id categoryId, u.user_id userId, nickname, content, count(l.post_id) likes from post p join user u on p.user_id = u.user_id join content c on p.content_id = c.content_id left outer join taku.like l on p.post_id = l.post_id WHERE board_id = #{boardId}
AND write_date > #{condition.period.startDate} AND write_date < #{condition.period.endDate}
AND p.user_id = ${condition.id.userId}
AND category_id = #{condition.id.categoryId}
AND post_title LIKE #{condition.text.title}
AND content LIKE #{condition.text.content}
GROUP BY p.post_id ORDER BY p.post_id desc
, likes desc
, views desc
LIMIT #{condition.limit.startIndex}, #{condition.limit.offset}
